import React, { Component } from 'react';
import { connect } from 'react-redux';
import DocsSlide from './DocsSlide';
import DocumentDetails from './DocumentDetails';
import { shopDocsListing, shopDocsListingSuccess } from '../../actions/shops';

class DocsListing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shopDocsList: props.shopDocsList,
      status: props.status,
      shop_id: this.props.match.params.index,
      activeView: 'list', // Track the current view ('list' or 'details')
      selectedDoc: null, // Store the selected document for details view
    };
    this.updateDocs = this.updateDocs.bind(this);
    this.showDetails = this.showDetails.bind(this); // Bind the showDetails function
    this.goBack = this.goBack.bind(this); // Bind the goBack function
  }

  componentDidMount() {
    this.fetchRecords();
  }

  fetchRecords() {
    shopDocsListing({ shop_id: this.state.shop_id }).then((response) => {
      console.log("Backend Response:", response.data.data);
      this.props.shopDocsListingSuccess(response.data.data);
    });
  }

  componentWillReceiveProps(nextProps) {
    if (this.props.status !== nextProps.status && nextProps.compName === 'shopdocs') {
      this.setState({
        shopDocsList: nextProps.shopDocsList,
        status: nextProps.status,
      });
    }
  }

  updateDocs(result) {
    window.$$('body').removeClass('modal-open');
    window.$$('body').find('.modal-backdrop').removeClass('modal-backdrop');
    this.hide();
  }

  hide() {
    window.getFooter().setState({
      renderElement: null,
    });
  }

  showDetails(doc) {
    this.setState({ activeView: 'details', selectedDoc: doc });
  }

  goBack() {
    this.setState({ activeView: 'list', selectedDoc: null });
  }

  render() {
    const { shopDocsList, activeView, selectedDoc } = this.state;

    // Render the details view if 'details' is the active view
    if (activeView === 'details' && selectedDoc) {
      return (
        <div>
          <button onClick={this.goBack} style={{ marginBottom: '20px', cursor: 'pointer' }}>Back to List</button>
          <DocumentDetails
            docType={selectedDoc.docs_type}
            docName={selectedDoc.docs_type}
            link={selectedDoc.link_to_file || selectedDoc.links_to_menu}
            images={selectedDoc.files || []}
            expiryDate={selectedDoc.expiry_date ? moment(selectedDoc.expiry_date).format('LL') : ''}
          />
        </div>
      );
    }

    // Render the list view by default
    return (
      <div className="right-ui-block">
        <div className="scrollspy-example">
          <div className="rm-content">
            <div className="row menu-top-block">
              <div className="col-sm-5 tl-block align-self-center">
                <h4 className="heading">Restaurant Documents</h4>
              </div>
            </div>

            <div className="row">
              <div className="col-sm-12">
                <div className="result-listing">
                  <table>
                    <thead>
                      <tr>
                        <th>Sr.no.</th>
                        <th>Name</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {shopDocsList.length > 0 ? (
                        shopDocsList.map((doc, index) => (
                          <DocsSlide
                            key={index}
                            slideData={doc}
                            index={index}
                            onNameClick={this.showDetails} // Pass the showDetails function as a prop
                          />
                        ))
                      ) : (
                        <tr>
                          <td colSpan="3">No records found</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  shopDocsList: state.Shop.shop_docs,
  status: state.Shop.status,
  compName: state.Shop.compName,
});

const mapDispatchToProps = (dispatch) => ({
  shopDocsListingSuccess: (payload) => dispatch(shopDocsListingSuccess(payload)),
});

export default connect(mapStateToProps, mapDispatchToProps)(DocsListing);
