      <> 
      <div className="fields-ui-block promocode-ui">
        <div className="basic-details">
          <div className="heading">
            <h4>Basic Information</h4>
          </div>
          <div className="form-block">

            <div className="row">
              <div className="col-lg-6">
                <Field
                  name="title"
                  component={renderField}
                  type="text"
                  className="form-control"
                  label="Rule Name"
                  placeholder=""
                />
              </div>
              <div className="col-lg-6">
                <Field
                  name="description"
                  component={renderField}
                  type="text"
                  className="form-control"
                  label="Rule Description"
                  placeholder=""
                />
              </div>
            </div>

            <div className="row">
              <div className="col-lg-4 selectbox-block">
                <Field
                  label="Coupon Type"
                  name="coupon"
                  optionLabel='label'
                  optionValue='value'
                  options={couponType.map((item) => ({
                    label: item.title,
                    value: item.id
                  }))}
                  component={renderReactSelect}
                  placeholder="Choose type"
                  multi={false}
                  className="select-ui"
                  parentDivClass="form-group w-100"
                  parentCallback={ this.couponChecked }
                />
              </div>
              <div className="form-group ri-block col-lg-6">
              <ul className="cs-check-box">
                <li>
                  <div className="os-check-box">
                   <Field
                    name="isBroadcast"
                    id="isBroadcast"
                    component="input"
                    type="checkbox"
                  //  onChange={ this.handleBroadcast }
                   onChange = {this.props.updateBroadcast}


                     />
                    <label for="isBroadcast">Broadcast coupon</label>
                    </div>
                </li>
              </ul>
            </div>

              <div className="col-lg-8 radio-ui-block radio-ui-margin">
                <Field 
                  type="radio" 
                  value="0"
                  name="isMerchantPromo"
                  id="s-promo" 
                  label="Admin Promo" 
                  disabled={isBroadcast == 2 ? true : false}
                  component={renderRadio}
                  onChange={ this.handleBearCost }
                />
                <Field 
                  type="radio" 
                  value="1"
                  name="isMerchantPromo"
                  id="d-promo" 
                  disabled={isBroadcast == 2 ? true : false}
                  label="Merchant Promo" 
                  component={renderRadio}
                  onChange={ this.handleBearCost }
                />
                    <Field 
                  type="radio" 
                  value="2"
                  name="isMerchantPromo"
                  id="b-promo" 
                  label="Both" 
                  component={renderRadio}
                  onChange={ this.handleBearCost }
                />
              </div>
            </div>
            { showUserType && (
              <div className='row'>
                  <div className="col-lg-12 selectbox-block">
                    <Field
                      label="User type"
                      name="userType"
                      optionLabel='label'
                      optionValue='value'
                      options={USER_TYPE}
                      component={renderReactSelect}
                      placeholder="Choose user type"
                      multi={false}
                      className="select-ui"
                      parentDivClass="form-group w-100"
                    parentCallback={this.userTypeChecked}

                    />
                   </div>
              </div>
           )}

             {/* ):( */}
             {showPhoneNumber && (
              <div className="row">
                  <div className="col-lg-12">
                    <Field
                      name="phoneNumber"
                      component={renderCreatableSelect}
                      type="text"
                      className=""
                      label="Add Phone Number(s)"
                      placeholder="e.g. 9817465720"
                    />
                  </div>
                </div>
                )}
          {/* )
 } */}
            {/* {bearCostBy == 0 && (
              <>
              <div className='row'>
                  <div className="col-lg-12 selectbox-block">
