              }
              {/*
              {
                itemInfo.newItem && itemInfo.newItem.fulladdons && itemInfo.newItem.fulladdons.length > 0 && 
                <>
                  <tr></tr>
                  <tr><td><h2>Addons</h2></td></tr>
