import React, { Component } from 'react';
import { toast } from 'react-toastify';
import { connect } from 'react-redux';
import Tooltip from '../common/Tooltip';
import { deleteShopDocument, deleteShopDocumentSuccess } from '../../actions/shops';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import Modal from '../../Modal';
import DocumentDetails from './DocumentDetails'; // Import the DocumentDetails component

class DocsSlide extends Component {
  constructor(props) {
    super(props);
    this.state = {
      slideData: props.slideData,
      index: props.index,
      showModal: false,  // Track whether to show the modal for details
    };
    this.editShopDocsPanel = this.editShopDocsPanel.bind(this);
    this.toggleModal = this.toggleModal.bind(this); // Bind the toggleModal function
  }
  editShopDocsPanel() {
    this.setState({ showModal: true });
  }

  toggleModal() {
    this.setState((prevState) => ({
      showModal: !prevState.showModal,
    }));
  }

  deleteDocs(itemId, index) {
    confirmAlert({
      title: '',
      message: 'Are you sure you want to delete this document?',
      buttons: [
        {
          label: 'Yes',
          onClick: () => {
            deleteShopDocument({ documentId: itemId }).then(() => {
              this.props.deleteShopDocumentSuccess(index);
              toast.success('Document deleted successfully!', {
                position: toast.POSITION.TOP_RIGHT,
              });
            });
          },
        },
        {
          label: 'No',
        },
      ],
    });
  }

  renderColumns() {
    const { slideData } = this.state;

    return (
      <>
        <td
          onClick={this.toggleModal}
          style={{ cursor: 'pointer', color: '#007bff' }}
        >
          {slideData.docs_type}
        </td>
      </>
    );
  }

  render() {
    const { slideData, index, showModal } = this.state;

    return (
      <>
        <tr>
          <td>{index + 1}</td>
          {this.renderColumns()}
          <td>
            <div className="table-btn-block">
              <Tooltip title="Edit">
                <button className="btn edit-btn" onClick={this.editShopDocsPanel}>
                  <i className="material-icons">edit</i>
                </button>
              </Tooltip>
              <Tooltip title="Delete">
                <button className="btn delete-btn" onClick={() => this.deleteDocs(slideData.id, index)}>
                  <i className="material-icons">delete</i>
                </button>
              </Tooltip>
            </div>
          </td>
        </tr>
        {showModal && (
          <Modal
            id="details-modal"
            show={true}
            onHide={this.toggleModal}
            header={<h4 className="modal-title">Document Details</h4>}
            body={<DocumentDetails docType={slideData.docs_type} docName={slideData.docs_type} link={slideData.link_to_file || slideData.links_to_menu} images={slideData.files || []} />}
          />
        )}
      </>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    deleteShopDocumentSuccess: (payload) => {
      dispatch(deleteShopDocumentSuccess(payload));
    },
  };
};

export default connect(null, mapDispatchToProps)(DocsSlide);
